	(6, '3', 'Are security management actions represented on dashboards and regularly reviewed?', 'QUESTION', '01A01-03', '01A01', '4', '', '', 'E2', '12.6.1', '', NULL),
	(7, '4', 'Is there a committee or structure bringing together all security managers, from all disciplines, in order to coordinate the various security related actions, take cross decisions, and meet regularly?', 'QUESTION', '01A01-04', '01A01', '2', '', '', 'R1', '12.6.1', '', NULL),
	(8, '5', 'Is the approach to security clearly recognized and supported by senior management?', 'QUESTION', '01A01-05', '01A01', '2', '', '', 'E2', '', '', NULL),
	(9, '2', 'Organization and Supervision of Information Systems Security', 'CATEGORY', '01A02', '01A', '', '', '', '', '', '', NULL),
	(10, '1', 'Is there a document describing the security policy related to information systems and in particular the organization, management and supervising of security (roles and responsibilities) as well as the fundamental principles underlying information security management?', 'QUESTION', '01A02-01', '01A02', '2', '', '', 'E1', '5.1.1', '5.1.1', NULL),
