	(20, '11', 'Does the organization improve knowledge about best security practices and maintain contacts with interest groups, associations, conferences, etc?', 'QUESTION', '01A02-11', '01A02', '1', '3', '', 'E2', '6.1.4; 12.6.1', '6.1.7', NULL),
