	(516, '2', 'Is the shutdown of any water evacuation system signaled immediately to a 24 hr monitoring center?', 'QUESTION', '03C03-02', '03C03', '2', '2', '3', 'E2', '', '', NULL),
	(517, '3', 'Are water evacuation systems regularly tested? ', 'QUESTION', '03C03-03', '03C03', '2', '', '3', 'E2', '', '', NULL),
