	(528, '2', 'Does the fire detection system have at least two types of detector (for example ionic and optical smoke detector)?', 'QUESTION', '03D02-02', '03D02', '4', '', '2', 'E2', '', '', NULL),
	(529, '3', 'Are the automatic detectors linked to a 24 hr monitoring center, equipped with a system which clearly identifies the location of the alarms which have been set off?', 'QUESTION', '03D02-03', '03D02', '2', '', '', 'E2', '', '', NULL),
	(530, '4', 'Is any shutdown of fire detection systems systematically signaled to a 24 hr monitoring center?', 'QUESTION', '03D02-04', '03D02', '4', '2', '', 'R1', '', '', NULL),
	(531, '5', 'Is the detection equipment regularly tested? and are procedures and intervention capabilities regularly audited?', 'QUESTION', '03D02-05', '03D02', '2', '3', '', 'C1', '', '', NULL),
	(532, '6', 'Are procedures and intervention capabilities regularly audited?', 'QUESTION', '03D02-06', '03D02', '2', '3', '', 'C1', '', '', NULL),
