	(545, '4', 'MEHARI_ExtendedNetwork', 'QUESTIONNAIRE', '04', '', '', '', '', '', '', '', '{\n  "category": "04",\n  "index": "4",\n  "type": "MEHARI_ExtendedNetwork",\n  "questions": [\n    {\n      "category": "04A",\n      "index": "4",\n      "type": "CATEGORY",\n      "value": "Security of the Wide-area Network architecture and service continuity",\n      "children": [\n        {\n          "category": "04A01",\n          "index": "1",\n          "type": "CATEGORY",\n          "value": "Functional security of the Wide-area Network architecture",\n          "children": [\n            {\n              "category": "04A01-01",\n              "index": "1",\n              "type": "QUESTION",\n              "value": "Have the requirements of service continuity of the Wide-area Network been analyzed and, if necessary, has a redundant architecture been determined for network connection points, equipment and network meshing?",\n              "children": [],\n              "gasf": [],\n              "answers": {\n                "MEHARI_W": "4",\n                "MEHARI_Min": "",\n                "MEHARI_ISO13": "",\n                "MEHARI_ISO13_info": "[]",\n                "MEHARI_Max": "2",\n                "MEHARI_ISO5": ""\n              },\n              "referenceObject": null,\n              "identifier": null,\n              "objType": "Audit",\n              "lockedBy": null\n            },\n            {\n              "category": "04A01-02",\n              "index": "2",\n              "type": "QUESTION",\n              "value": "Has a systematic search for single points of failure been carried out and has it been ensured that subsidiary equipment (power supply and air conditioning etc.) does not introduce or eliminate the redundancy expected in the equipment or network architecture?",\n              "children": [],\n              "gasf": [],\n              "answers": {\n                "MEHARI_W": "4",\n                "MEHARI_Min": "",\n                "MEHAR