	(879, '9', 'Are the elements enabling the detection of an anomaly or incident archived (on disk, cassette or optical disk etc.)?', 'QUESTION', '05D01-09', '05D01', '2', '', '', 'E2', '12.4.2', '10.10.3', NULL),
	(880, '10', 'Are the monitoring procedures of the local area network, the detection of the anomalies and the availability of the surveillance team subject to regular audit?', 'QUESTION', '05D01-10', '05D01', '2', '3', '', 'C1', '', '', NULL),
