	(890, '9', 'Are the records and summary analysis kept for a long period?', 'QUESTION', '05D02-09', '05D02', '2', '', '', 'E2', '12.4.2', '10.10.3', NULL),
	(891, '10', 'Are the procedures for recording, processing and analysis of the records and summaries as well as the availability of the analysis and corrective team subject to regular audit?', 'QUESTION', '05D02-10', '05D02', '2', '', '', 'C1', '', '', NULL),
	(892, '3', 'Management of incidents on the local area network ', 'CATEGORY', '05D03', '05D', '', '', '', '', '', '', NULL),
