	(894, '2', 'Is this hot-line available 24 hours a day?', 'QUESTION', '05D03-02', '05D03', '2', '', '', 'E2', '12.4.1; 16.1.5', '10.10.5', NULL),
	(895, '3', 'Is there a system to support incident management?', 'QUESTION', '05D03-03', '05D03', '2', '2', '', 'E2', '12.4.1; 16.1.5', '10.10.5', NULL),
	(896, '4', 'Does this system centralize incidents detected both by operational personnel and by users?', 'QUESTION', '05D03-04', '05D03', '2', '', '', 'E2', '12.4.1; 16.1.5', '10.10.5', NULL),
	(897, '5', 'Does this system cater for a systematic follow-up of necessary actions?', 'QUESTION', '05D03-05', '05D03', '4', '', '', 'E3', '12.4.1; 16.1.5', '10.10.5', NULL),
