	(1506, '3', 'Are the archive storage locations under video surveillance when not occupied?\r\n', 'QUESTION', '08H03-03', '08H03', '2', '3', '', 'E2', '', '', NULL),
	(1507, '4', 'Are the archive storage locations under permanent video surveillance?\r\n', 'QUESTION', '08H03-04', '08H03', '2', '3', '', 'E2', '', '', NULL),
	(1508, '5', 'Are archive media labeled without reference to the content?', 'QUESTION', '08H03-05', '08H03', '3', '', '', 'E2', '', '', NULL),
	(1509, '6', ' Are the index tables used to associate content to an archive media number  inaccessible to the staff managing the archive media?', 'QUESTION', '08H03-06', '08H03', '3', '3', '', 'E2', '', '', NULL),
	(1510, '7', 'Are these index tables backed up?', 'QUESTION', '08H03-07', '08H03', '3', '3', '', 'E2', '', '', NULL),
