	(1649, '5', 'Have the appropriate instructions and training been given to staff in order to deal with a manual emergency solution?', 'QUESTION', '09E02-05', '09E02', '2', '', '', 'E2', '17.1.2', '14.1.3', NULL),
	(1650, '6', 'Have the application service continuity plans anticipated all the procedures required for a return to normal service?', 'QUESTION', '09E02-06', '09E02', '4', '2', '', 'E2', '17.1.2', '14.1.3', NULL),
