	(2149, '2', 'Are these lists protected against untimely or illicit alteration by a robust sealing process?', 'QUESTION', '12D01-02', '12D01', '2', '', '', 'R2', '', '', NULL),
	(2150, '3', 'Are the rights provided to users preventing them to modify the specific telecommunication (telephone, audio or video conferencing, etc. )configurations of their equipment?', 'QUESTION', '12D01-03', '12D01', '4', '', '', 'E2', '', '', NULL),
