	(2154, '2', 'Training and awareness setting for users ', 'CATEGORY', '12D02', '12D', '', '', '', '', '', '', NULL),
	(2155, '1', 'Are the users made aware of the risks of  tapping resulting from the use of telecommunication equipment and services?', 'QUESTION', '12D02-01', '12D02', '4', '', '', 'E1', '', '', NULL),
	(2156, '2', 'Are the users informed of risks resulting from the malevolent usage, locally or remotely, of their communication equipment?\r\nIn addition to the use of their colleagues` equipment  without their knowing.', 'QUESTION', '12D02-02', '12D02', '4', '', '', 'E2', '', '', NULL),
